package controlstatement;

public class LoopsEvenNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


				for(int i=0;i<50;i++)

				{

					if(i%2==0)// for even no
						      //(i%2!=0) for odd no

					System.out.println(i);

				}

				int sum=0;

				for(int i=0;i<10;i++)

				{

					sum=sum+i;

					//System.out.println(i);

				}

				System.out.println("sum is "+sum);

			}
	}


