package patternmatching;

public class Alphabetpattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 char ch='a';
		 for (int i=1;i<5;i++) {
		    	for(int j=1;j<=5;j++) {
		    		System.out.print(ch+ " ");
		    		ch++;
		    }
		    	
		    System.out.println();
		 }
	}
}

