package patternmatching;

public class pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;

		for(int i=1;i<=n;i++)

		{

			for(int j=i;j<=n;j++)

			{

				System.out.print(j+" ");

			}

			System.out.println();

		}
	}

}/*
1 2 3 4 5 
2 3 4 5 
3 4 5 
4 5 
5 
*/
